import React, { useEffect } from "react";
import { createSelector } from "@reduxjs/toolkit";
import { useSelector, useDispatch } from "react-redux";
import { getCinema } from "../slices/cinemaSlice";
const movieSelector = createSelector(
  (state) => state,
  (state) => state.cinema
);

export default function Cinema() {
  const dispatch = useDispatch();
  const { cinemas, isLoading, error } = useSelector(movieSelector);
  useEffect(() => {
    dispatch(getCinema());
  }, []);
  return (
    <div>
      <div className="cumRap">
        <div className="container">
          <div className="row">
            <div className="col-1">
              {cinemas.map((item, index) => {
                <div key={index}>
                  <img src={item.logo} alt="" width="50px" height="50px" />
                  <div>{item.maHeThongRap} </div>
                </div>;
              })}
            </div>
            <div className="col-3">
              <h1>ra</h1>
            </div>
            <div className="col-8">
              <h1>fgh</h1>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
