import React, { useEffect } from "react";
import { createSelector } from "@reduxjs/toolkit";
import { useSelector, useDispatch } from "react-redux";
import { getCinema } from "../slices/cinemaSlice";
import { getMovies } from "../slices/movieSlice";
const movieSelector = createSelector(
  (state) => state,
  (state) => state.movie
);

export default function Cinema() {
  const dispatch = useDispatch();
  const { movies, isLoading, error } = useSelector(movieSelector);
  useEffect(() => {
    dispatch(getMovies());
  }, []);
  return (
    <div>
      {isLoading && <div>Loading...</div>}
      {error && <div>{error}</div>}
      <div className="cumRap">
        <div className="container">
          <div className="row">
            <div className="col-1">
              {movies.map((item, index) => {
                <div key={index}>
                  <div>{item.maPhim} </div>
                </div>;
              })}
            </div>
            <div className="col-3">
              {/* {cinemas.map((item, index) => {
                <div key={index}>
                  <div>{item.maHeThongRap} </div>
                </div>;
              })} */}
            </div>
            <div className="col-8">
              <h1>fgh</h1>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
