import React, { useState } from "react";
import "../../../styles/css/news.css";
import Newx from "../News";
export default function News() {
  const [active, setActive] = useState("a");
  const handle = () => {
    setActive("a");
  };
  const handle2 = () => {
    setActive("b");
  };
  const handle3 = () => {
    setActive("c");
  };
  console.log(active);
  return (
    <div id="lichchieu">
      <div className="top__background">
        <img src="../img/back-news.png" alt="" />
      </div>
      <div className="container ">
        <div className="button__Show__News " style={{ textAlign: "center" }}>
          <button
            className="buttonShowing"
            onClick={handle}
            disabled={active === "a"}
          >
            <h2>Điện Ảnh 24h</h2>
          </button>
          <button
            className="buttonShowing"
            onClick={handle2}
            disabled={active === "b"}
          >
            <h2>Review</h2>
          </button>
          <button
            className="buttonShowing"
            onClick={handle3}
            disabled={active === "c"}
          >
            <h2>Khuyến Mãi</h2>
          </button>
        </div>
        <Newx />
      </div>
    </div>
  );
}
