import React, { useState } from "react";
import "../../../styles/css/news.css";
export default function News() {
  const [active, setActive] = useState("a");
  const handle = () => {
    setActive(!active);
  };
  return (
    <div style={{ paddingTop: "80px" }} id="lichchieu">
      <div className="top__background">
        <img src="../img/back-news.png" alt="" />
      </div>
      <div className="container w-100">
        <div className="button__Show__News " style={{ textAlign: "center" }}>
          <button className="buttonShowing" onClick={handle} disabled={active}>
            <h2>Điện Ảnh 24h</h2>
          </button>
          <button className="buttonShowing" onClick={handle} disabled={active}>
            <h2>Review</h2>
          </button>
          <button className="buttonShowing" onClick={handle} disabled={active}>
            <h2>Khuyến Mãi</h2>
          </button>
        </div>
      </div>
    </div>
  );
}
