import React from "react";
import useStyles from "./style";
import "../../../styles/css/movieItem.css";
import { createSelector } from "@reduxjs/toolkit";
import { useSelector, useDispatch } from "react-redux";
import { getMovies } from "../slices/movieSlice";
const movieSelector = createSelector(
  (state) => state,
  (state) => state.movie
);
function MovieItem() {
  const classes = useStyles({ bg: hinhAnh });
  const dispatch = useDispatch();
  const { movies, isLoading, error } = useSelector(movieSelector);
  useEffect(() => {
    dispatch(getMovies());
  }, []);
  return (
    <div>
      {isLoading && <div>Loading...</div>}
      {error && <div>{error}</div>}
      {movies.map(
        (movie) =>
          movie.dangChieu === true && (
            <div key={movie.maPhim}>
              <div
                style={{
                  padding: "7px",
                  cursor: "pointer",
                }}
              >
                <div className="film">
                  <div className="film__img">
                    <div className={`film__poster ${classes.addbg}`}>
                      <div
                        className="film__overlay"
                        onClick={() => history.push(`/movie/${movieId}`)}
                      />
                    </div>
                  </div>
                  <div className="film__content">
                    <div className={`film__name `}>
                      <div className="name">
                        <p>
                          <span className="c18">C18</span>
                          {movie.tenPhim}
                        </p>
                      </div>
                      <p>
                        <span className="text_info">ph√∫t - Tix</span>
                      </p>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          )
      )}
    </div>
  );
}
export default MovieItem;
