import React, { useEffect } from "react";
import { createSelector } from "@reduxjs/toolkit";
import { useSelector, useDispatch } from "react-redux";
import { useHistory } from "react-router-dom";
import { getMovies } from "../slices/movieSlice";
import "../../../styles/css/MovieShowing.css";
import useStyles from "./style";
import ArrowBackIosRoundedIcon from "@material-ui/icons/ArrowBackIosRounded";
import ArrowForwardIosRoundedIcon from "@material-ui/icons/ArrowForwardIosRounded";
import Slider from "react-slick";
// Sử dụng createSelector để ghi nhớ lại giá trị hiện tại của selector, khi redux store thay đổi nó sẽ so sánh giá trị hiện tại của selector với giá trị mới của redux store, nếu không có sự khác biệt nó sẽ không cần phải tính toán lại
// Lưu ý: tuy nhiên ta chỉ nên sử dụng createSelector khi ta select state từ redux store và có những tính toán phức tạp trước khi return về data
const movieSelector = createSelector(
  (state) => state,
  (state) => state.movie
);

export function NextArrow(props) {
  const classes = useStyles();
  const { onClick } = props;
  return (
    <ArrowForwardIosRoundedIcon
      style={{ right: "-82px" }}
      onClick={onClick}
      className={classes.Arrow}
    />
  );
}

export function PrevArrow(props) {
  const classes = useStyles();
  const { onClick } = props;
  return (
    <ArrowBackIosRoundedIcon
      style={{ left: "-82px" }}
      onClick={onClick}
      className={classes.Arrow}
    />
  );
}

export default function MovieShowing() {
  const history = useHistory();
  const dispatch = useDispatch();
  const { movies, isLoading, error } = useSelector(movieSelector);

  // Khi ta để logic gọi API trực tiếp trong component sẽ có 2 vấn đề
  // - Component sẽ chứa cả logic lần UI => file sẽ khá dài
  // - Không có khả năng tái sử dụng..
  useEffect(() => {
    dispatch(getMovies());
  }, []);
  const settings = {
    className: "center",
    centerPadding: "60px",
    slidesToShow: 1,
    speed: 500,
    rows: 2,
    slidesPerRow: 4,
    nextArrow: <NextArrow />,
    prevArrow: <PrevArrow />,
  };
  return (
    <div className="container">
      <Slider {...settings}>
        <div className="tittle">
          <button>
            <h2>Đang Chiếu</h2>
          </button>
          <button>
            <h2>Sắp Chiếu</h2>
          </button>
        </div>
        {isLoading && <div>Loading...</div>}
        {error && <div>{error}</div>}
        <div className="cards">
          {movies.map(
            (movie) =>
              movie.dangChieu === true && (
                <div key={movie.maPhim}>
                  <div className="card">
                    <div className="card__image-container">
                      <img className="card-img" src={movie.hinhAnh} alt="" />
                    </div>
                    <div class="card__content">
                      <p class="card__title text--medium">{movie.tenPhim}</p>
                    </div>
                    <div class="card__info">
                      <p class="text--medium">100 phút </p>
                    </div>
                  </div>
                </div>
              )
          )}
        </div>
      </Slider>
    </div>
  );
}
