import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import movieAPI from "../../../services/movieAPI";

const initialState = {
  movies: [],
  isLoading: false,
  error: null,
};

// Tạo action thông qua createAsyncThunk
export const getMovies = createAsyncThunk("banner/getMovies", async () => {
  const data = await movieAPI.getBanner();
  return { data };
});

const bannerSlice = createSlice({
  name: "banner",
  initialState,
  reducers: {
    // getMovieRequest(state, action) {
    //   state.isLoading = true;
    // },
    // getMovieSuccess(state, action) {
    //   state.movies = action.payload.data;
    //   state.isLoading = false;
    // },
    // getMovieFailure(state, action) {
    //   state.error = action.payload.error;
    //   state.isLoading = false;
    // },
  },
  extraReducers: {
    [getMovies.pending]: (state, action) => {
      state.isLoading = true;
    },
    [getMovies.fulfilled]: (state, action) => {
      state.movies = action.payload.data;
      state.isLoading = false;
    },
    [getMovies.rejected]: (state, action) => {
      state.error = action.error.message;
      state.isLoading = false;
    },
  },
});

// export const { getMovieRequest, getMovieSuccess, getMovieFailure } =
//   movieSlice.actions;

export default bannerSlice.reducer;
