import React, { useState } from "react";

export default function News() {
  const [active, setActive] = useState(true);
  const handle = () => {
    setActive(!active);
  };
  return (
    <div style={{ paddingTop: "80px" }} id="lichchieu">
      <div className="top__background">
        <img src="../img/back-news.png" alt="" />
      </div>
      <div className="container w-100">
        <div className="buttonShowmovie " style={{ textAlign: "center" }}>
          <button
            className="buttonShowing"
            style={{ marginRight: "100px" }}
            onClick={handle}
            disabled={active}
          >
            <h2>Đang Chiếu</h2>
          </button>
          <button className="buttonShowing" onClick={handle} disabled={!active}>
            <h2>Sắp Chiếu</h2>
          </button>
        </div>
        <MovieItem active={active} />
      </div>
    </div>
  );
}
