import React, { useState } from "react";
import ItemDiaChi from "../RapChieuPhim/itemDiaChi";
import ItemRapChieuPhim from "../RapChieuPhim/itemLogo";
export default function Cinema() {
  function renderLogo(logo) {
    let duLieu = "";
    switch (logo.maHeThongRap) {
      case "BHDStar":
        {
          duLieu = "BHDStar";
        }
        break;
      case "CGV":
        {
          duLieu = "CGV";
        }
        break;
      case "CineStar":
        {
          duLieu = "CineStar";
        }
        break;
      case "Galaxy":
        {
          duLieu = "Galaxy";
        }
        break;
      case "LotteCinima":
        {
          duLieu = "LotteCinima";
        }
        break;
      case "MegaGS":
        {
          duLieu = "MegaGS";
        }
        break;
    }
    console.log(logo.maHeThongRap);
    setA(duLieu);
  }
  const [a, setA] = useState("BHDStar");
  return (
    <div>
      <div className="cumRap">
        <div className="img"></div>
        <div className="container">
          <div className="row m-auto " style={{ maxWidth: "940px" }}>
            <div className="col-1">
              <ItemRapChieuPhim data={renderLogo} />
            </div>
            <div className="col-4">
              <ItemDiaChi param={a} />
            </div>
            <div className="col-7" style={{ textAlign: "center" }}>
              <h4>Không có suất chiếu</h4>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
