import React, { useState } from "react";
import "./demo.css";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import { createSelector } from "@reduxjs/toolkit";
import { useSelector, useDispatch } from "react-redux";
import { getMovies } from "../slices/movieSlice";
import { useHistory } from "react-router-dom";
const movieSelector = createSelector(
  (state) => state,
  (state) => state.movie
);
export default function MovieItem2() {
  const [active, setActive] = useState(1);
  const dispatch = useDispatch();
  const { movies, isLoading, error } = useSelector(movieSelector);
  const history = useHistory();
  useEffect(() => {
    dispatch(getMovies());
  }, []);
  var settings = {
    infinite: true,
    speed: 500,
    slidesToShow: 4,
    slidesToScroll: 4,
    rows: 2,
  };

  return (
    <div className="wrapper">
      {isLoading && <div>Loading...</div>}
      {error && <div>{error}</div>}
      <div className="tabs">
        <TabItemComponent
          key={title}
          icon={icon}
          title={title}
          onItemClicked={() => setActive(id)}
          isActive={active === id}
        />
      </div>
      <div className="content">
        <Slider {...settings}>
          {movies.map((item, index) => {
            return active === 1 && item.dangChieu === true ? (
              <div key={index} className="row">
                <div
                  className="flim"
                  onClick={() => history.push(`/movie/${movie.maPhim}`)}
                >
                  <img src={item.hinhAnh} alt="" width="100%" height="100%" />
                </div>
                <div className="flimContent">
                  <span>{item.tenPhim}</span>
                </div>
                <div className="thoiLuong">
                  <p>100 phút</p>
                </div>
              </div>
            ) : (
              ""
            );
          })}
        </Slider>
      </div>
      <div className="content">
        <Slider {...settings}>
          {movies.map((item, index) => {
            return active === 2 && item.sapChieu === true ? (
              <div key={index} className="row">
                <div className="flim">
                  <img src={item.hinhAnh} alt="" width="100%" height="100%" />
                </div>
                <div className="flimContent">
                  <span>{item.tenPhim}</span>
                </div>
                <div>
                  <p>100 phút</p>
                </div>
              </div>
            ) : (
              ""
            );
          })}
        </Slider>
      </div>
    </div>
  );
}
const TabItemComponent = ({
  icon = "",
  title = "",
  onItemClicked = () => console.error("You passed no action to the component"),
  isActive = 0,
}) => {
  return (
    <div
      className={isActive ? "tabitem" : "tabitem tabitem--inactive"}
      onClick={onItemClicked}
    >
      <i className={icon}></i>
      <p className="tabitem__title">{title}</p>
    </div>
  );
};
