import React, { useEffect } from "react";
import { createSelector } from "@reduxjs/toolkit";
import { useSelector, useDispatch } from "react-redux";
import { getCinema } from "../slices/cinemaSlice";
const movieSelector = createSelector(
  (state) => state,
  (state) => state.cinema
);

export default function Cinema() {
  const dispatch = useDispatch();
  const { cinemas, isLoading, error } = useSelector(movieSelector);
  useEffect(() => {
    dispatch(getCinema());
  }, []);
  return (
    <div>
      {isLoading && <div>Loading...</div>}
      {error && <div>{error}</div>}
      <div className="cumRap">
        <div className="container">
          <div className="row">
            <div className="col-1">
              {cinemas.map((movie, index) => (
                <div key={index}>
                  <img
                    src={movie.logo}
                    alt=""
                    alt=""
                    width="50px"
                    height="50px"
                  />
                </div>
              ))}
            </div>
            <div className="col-3">
              {/* {cinemas.map((item, index) => {
                <div key={index}>
                  <div>{item.maHeThongRap} </div>
                </div>;
              })} */}
            </div>
            <div className="col-8">
              <h1>fgh</h1>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
